import sys
import argparse


def commandLine_parser():
    parser = argparse.ArgumentParser()
    parser.add_argument( '-i', required=True, type=str, dest='obs_id', help='Enter an observation ID, Starting Letter of Observation ID Expected a L (Like L85001), required=Yes')
    parser.add_argument( '-p', required=True, type=str, dest='product', choices=['calib', 'nocalib'], help='Please Enter Product which You Want as a (calib or nocalib), required=Yes')

    args = parser.parse_args()

    if args.obs_id[0] != 'L' :
        raise RuntimeError( "Error in Parsing 1st Command Line Arguments:\nStarting Letter of Observation ID Expected a L (Like L85001)\n")

    return args


def run_me(args=None):
    """The main routine."""
    if args is None:
        args = sys.argv[1:]

    print("This is the main routine.")
    print("It should do something interesting.")

    args = commandLine_parser()

    # Do argument parsing here (eg. with argparse) and anything else
    # you want your project to do.

if __name__ == "__main__":
    run_me()
